#!/usr/bin/env python3


def parser(text) -> str:
    return text.strip()


def read_input() -> str:
    with open(__file__, encoding="utf-8") as f:
        c = f.read()
        data = c[c.rindex("🎅") + 1: c.rindex("⛄")].strip()
    return data


# MAIN FUNCTIONS
def part1(given: str) -> int:
    # ord('(') = 40 and ord(')') = 41
    return sum(1 if floor == '(' else -1
               for floor in given)


def part2(given: str) -> int:
    floor = 0
    for i, ch in enumerate(given):
        floor += 1 if ch == '(' else -1
        if floor == -1:
            return i + 1
    return -1


# TEST
def test():
    # GIVEN
    assert part1("(())") == part1("()()") == 0
    assert part1("(((") == part1("(()(()(") == part1("))(((((") == 3
    assert part1("())") == part1("))(") == -1
    assert part1(")))") == part1(")())())") == -3
    # part 2
    assert part2(")") == 1
    assert part2("()())") == 5

    return True


if __name__ == '__main__':
    assert test()
    given = parser(read_input())
    # ONE #1
    part_1 = part1(given)
    print(part_1)
    assert part_1 == 74
    # # TWO #2
    part_2 = part2(given)
    print(part_2)
    assert part_2 == 1795

